package com.hfsy.common.exception;import java.io.Serializable;/** * @description: 业务异常（由异常状态码区分不同的业务异常） * @author: jensen * @date: created by 2019/9/11 0:36 */public class CommonBizException extends RuntimeException implements Serializable {    private ExpCodeEnum codeEnum;    public CommonBizException(ExpCodeEnum codeEnum) {        super(codeEnum.getMessage());        this.codeEnum = codeEnum;    }    public CommonBizException() {    }    public ExpCodeEnum getCodeEnum() {        return codeEnum;    }}