package com.hfsy.common.exception;import java.io.Serializable;import static com.hfsy.common.utils.ExpPrefixUtil.ComExpPrefix;/** * @description: 全局的异常状态码 和 异常描述 * PS:异常码一共由5位组成，前两位为固定前缀，请参考{@link com.hfsy.common.utils.ExpPrefixUtil} * @author: jensen * @date: created by 2019/9/11 0:30 */public enum ExpCodeEnum implements Serializable {    /**     * 通用异常     */    UNKNOW_ERROR(ComExpPrefix + "000", "未知异常"),    ERROR_404(ComExpPrefix + "001", "没有该接口"),    PARAM_NULL(ComExpPrefix + "002", "参数为空"),    NO_REPEAT(ComExpPrefix + "003", "请勿重复提交"),    SESSION_NULL(ComExpPrefix + "004", "请求头中SessionId不存在"),    HTTP_REQ_METHOD_ERROR(ComExpPrefix + "005", "HTTP请求方法不正确"),    JSONERROR(ComExpPrefix + "006", "JSON解析异常"),    REDISERROR(ComExpPrefix + "007", "REDIS解析异常"),    SAVE_FAIL(ComExpPrefix + "008", "保存失败"),    /**     * 模块异常     */    ;    private String code;    private String message;    private ExpCodeEnum(String code, String message) {        this.code = code;        this.message = message;    }    ExpCodeEnum() {    }    public String getCode() {        return code;    }    public String getMessage() {        return message;    }}