package com.hfsy.module.service.impl;import com.hfsy.common.result.Result;import com.hfsy.module.service.KafkaService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.kafka.core.KafkaTemplate;import org.springframework.kafka.support.SendResult;import org.springframework.stereotype.Service;import org.springframework.util.concurrent.ListenableFuture;/** * @description: kafka 服务 * @author: jensen * @date: created by 2019/9/15 13:27 */@Servicepublic class KafkaServiceImpl implements KafkaService {    @Autowired    private KafkaTemplate<String, Object> kafkaTemplate;    @Override    public Result send(String topic,String msg) {        kafkaTemplate.send(topic, msg);        return Result.newSuccessResult();    }}